import React from 'react';
import { connect } from 'react-redux';
import './counter.styles.scss';
import Button from '../button/button.component'; 
import { countUp , countDown } from '../../action/index';
 
const Counter = ({countUp , countDown }) => {
    // constructor(){
    //     super()

    //     this.state={
    //         num:0
    //     };
    // }

    // Plus = e => {
    //     const number = this.state.num + 1
    //     this.setState({num:number})
    // }   

    // Minus = e => {
    //     const number = this.state.num - 1
    //     this.setState({num:number})
    // }

        return(
            
            <div className='wrap'>  

                <Button 
                    handleClick={dispatch(countUp())}
                    children='増'
                />
                <div id='display'>{getState()}</div>
                <Button 
                    handleClick={dispatch(countDown())}
                    children='減'
                />
                
            </div>

        )

}

export default connect(null,{ countUp,countDown })(Counter);
