const initialState={
    num:0
}

export default function counters (state=initialState,action){
    switch (action.type) {
        case 'COUNT_UP':
            return state = state.num + 1
 
        case 'COUNT_DOWN':
            return state.num - 1
        default:
            return state.num   

    }
}
