import React from 'react';
import { connect } from 'react-redux';
import './counter.styles.scss';
import Button from '../button/button.component'; 
import { countUp , countDown } from '../../action.index';
 
class Counter extends React.Component {
    constructor(){
        super()

        this.state={
            num:0
        };
    }

    Plus = e => {
        const number = this.state.num + 1
        this.setState({num:number})
    }   

    Minus = e => {
        const number = this.state.num - 1
        this.setState({num:number})
    }

    render(){
        return(
            
            <div className='wrap'>  

                <Button 
                    handleClick={this.Plus}
                    children='増'
                />
                <div id='display'>{this.state.num}</div>
                <Button 
                    handleClick={this.Minus}
                    children='減'
                />
                
            </div>

        )
    }
}

export default connect(null,{countUp,countDown })(Counter);
