// const initialState={
//     num:0
// }

export default function counters (state,action){
    switch (action.type) {
        case 'COUNT_UP':
            return console.log(state++) 
 
        case 'COUNT_DOWN':
            return state.num - 1
        default:
            return state.num   

    }
}
